,p_attribute_01=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'    NEW_RESERVATION_CD varchar2(20);',
'    soba varchar2(10);',
'    ex_no_room exception;',
'    breakfast_price number;',
'    err_msg varchar2(100);',
'    sent_mail_status boolean;',
'begin',
'',
'    savepoint no_new_reservation;',
'',
'    select to_number(value) into breakfast_price from e_lov where key = ''BREAKFAST_PRICE_PER_PERSON'' and type = ''HOTEL_REF'';',
'',
'    select room_num into soba from (select * from (select room_num from e_rooms where persons = to_number(:P7_ROOM_CAP)',
'          minus',
'          select rm.room_num from e_reservations r inner join e_reservation_rooms rm',
'          on r.reservation_cd = rm.reservation_cd',
'          inner join e_rooms rooms',
'          on rm.room_num = rooms.room_num',
'          where rooms.persons = to_number(:P7_ROOM_CAP) and',
'		  r.curr_status <> ''CANCELED'' and ',
'          not (((to_date(:P7_START_DATE,''DD.MM.YYYY.'') < r.start_dt and to_date(:P7_END_DATE,''DD.MM.YYYY.'') <= r.start_dt)',
'          or',
'          (to_date(:P7_START_DATE,''DD.MM.YYYY.'') >= r.end_dt and to_date(:P7_END_DATE,''DD.MM.YYYY.'') > r.end_dt)))) ',
'    order by DBMS_RANDOM.RANDOM)',
'    where rownum < 2;',
'',
'    if soba is null then',
'        RAISE ex_no_room;',
'    end if;',
'',
'',
'    INSERT INTO e_reservations (',
'        start_dt,',
'        end_dt,',
'        user_mail,',
'        breakfast_inc,',
'        price,',
'        num_of_guests,',
'        curr_status',
'    ) VALUES (',
'        to_date(:P7_START_DATE,''DD.MM.YYYY.''),',
'        to_date(:P7_END_DATE,''DD.MM.YYYY.''),',
'        :P7_EMAIL,',
'        :P7_BREAKFAST,',
'        case',
'            when :P7_BREAKFAST = ''Y'' then to_number(:P7_PRICE_WITHOUT_BREAKFAST, ''99G999'') + to_number(:P7_GUEST_NUM) * (to_date(:P7_END_DATE,''DD.MM.YYYY.'') - to_date(:P7_START_DATE,''DD.MM.YYYY.'')) * breakfast_price',
'            when :P7_BREAKFAST = ''N'' then to_number(:P7_PRICE_WITHOUT_BREAKFAST, ''99G999'')',
'        end,',
'        to_number(:P7_GUEST_NUM),',
'        ''CREATED'' ',
'    )',
'    RETURNING RESERVATION_CD into NEW_RESERVATION_CD;',
'',
'    iNSERT INTO e_reservation_rooms (',
'        reservation_cd,',
'        room_num',
'    ) VALUES (',
'        NEW_RESERVATION_CD,',
'        soba',
'    );',
'',
'    commit;',
'    select ''RES_SUC'' into :P7_RESERVATIOM_RESULT from dual;',
'',
'    -- MAIL_SENDING_TEMPLATES.send_successful_reservation ',
'    -- (   MAIL => :P7_EMAIL,',
'    --     RESERVATION_CD => NEW_RESERVATION_CD,',
'    --     SENT => sent_mail_status',
'    -- ) ;',
'',
'	WRAPPERS_MAIL_SENDING_TEMPLATES2.send_successful_reservation',
'	(   ',
'		MAIL => :P7_EMAIL,',
'        RESERVATION_CD => NEW_RESERVATION_CD',
'    );',
'',
'    exception',
'        when ex_no_room then',
'            select ''NO_ROOM'' into :P7_RESERVATIOM_RESULT from dual;',
'        when others then',
'            rollback to savepoint no_new_reservation;',
'            -- select ''NO_RES'' into :P7_RESERVATIOM_RESULT from dual;',
'            err_msg := SUBSTR(SQLERRM, 1, 200);',
'            select err_msg into :P7_RESERVATIOM_RESULT from dual;',
'',
'',
'',
'end;'))
,p_attribute_02=>'P7_EMAIL,P7_START_DATE,P7_END_DATE,P7_ROOM_CAP,P7_BREAKFAST,P7_PRICE_WITHOUT_BREAKFAST,P7_PRICE_WITH_BREAKFAST,P7_GUEST_NUM'
,p_attribute_03=>'P7_RESERVATIOM_RESULT'
,p_attribute_04=>'N'
,p_attribute_05=>'PLSQL'
,p_wait_for_result=>'Y'
);
